#!/bin/bash

cd /home/ibr-ai-agent/.openclaw/

# Check if there are any changes to commit
if [[ -n $(git status --porcelain) ]]; then
    echo "Changes detected, performing backup commit..."

    # Add all changes
    git add .

    # Get current date and time for commit message
    datetime=$(date '+%Y-%m-%d %H:%M:%S')

    # Commit with timestamp
    git commit -m "Backup commit ${datetime}"

    # Push to main branch
    if git push origin main; then
        echo "Backup completed successfully at ${datetime}"
    else
        # If push fails, send notification to Telegram
        echo "Git push failed"

        # Get the error details
        error_details=$(git push origin main 2>&1)

        # Send notification to Telegram using the message tool
        ERROR_DETAILS_ESCAPED=$(printf '%s\n' "$error_details" | sed -e 's/\\/\\\\/g' -e 's/"/\\"/g')
        /home/ibr-ai-agent/.nvm/versions/node/v24.13.0/bin/node -e "
        (async () => {
            const { message } = require('@openclaw/api');
            try {
                await message({
                    action: 'send',
                    channel: 'telegram',
                    message: 'Backup failed due to: ' + '${ERROR_DETAILS_ESCAPED}'
                });
            } catch (err) {
                console.error('Failed to send Telegram notification:', err);
            }
        })();
        "
    fi
else
    echo "No changes to commit, skipping backup."
fi

