const { exec } = require('child_process');
const { promisify } = require('util');
const { message } = require('@openclaw/api');

const execAsync = promisify(exec);

async function runGitBackup() {
    try {
        // Change to the .openclaw directory
        const { stdout: pwdOut } = await execAsync('pwd', { cwd: '/home/ibr-ai-agent/.openclaw/' });

        // Check if there are changes to commit
        const { stdout: statusOut } = await execAsync('git status --porcelain', { cwd: '/home/ibr-ai-agent/.openclaw/' });

        if (statusOut.trim()) {
            console.log('Changes detected, performing backup commit...');

            // Add all changes
            await execAsync('git add .', { cwd: '/home/ibr-ai-agent/.openclaw/' });

            // Get current date and time for commit message
            const datetime = new Date().toISOString();

            // Commit with timestamp
            await execAsync(`git commit -m "Backup commit ${datetime}"`, { cwd: '/home/ibr-ai-agent/.openclaw/' });
            console.log(`Committed changes at ${datetime}`);

            // Push to main branch
            try {
                const pushResult = await execAsync('git push origin main', { cwd: '/home/ibr-ai-agent/.openclaw/' });
                console.log(`Backup completed successfully at ${datetime}`);
                return true;
            } catch (pushError) {
                console.error('Git push failed:', pushError.message);

                // Send notification to Telegram
                try {
                    await message({
                        action: 'send',
                        channel: 'telegram',
                        message: `Backup failed due to: ${pushError.message}`
                    });
                } catch (notificationError) {
                    console.error('Failed to send Telegram notification:', notificationError);
                }

                return false;
            }
        } else {
            console.log('No changes to commit, skipping backup.');
            return true; // Success in the sense that there was nothing to do
        }
    } catch (error) {
        console.error('Error during git backup:', error.message);

        // Send notification to Telegram
        try {
            await message({
                action: 'send',
                channel: 'telegram',
                message: `Backup failed due to: ${error.message}`
            });
        } catch (notificationError) {
            console.error('Failed to send Telegram notification:', notificationError);
        }

        return false;
    }
}

// Execute the function
runGitBackup();

